import { useState } from "react";
import and from "../pics/android.png";
import apl from "../pics/apple.png";
import pwr from "../pics/power.png";
import "../style/ThirdQ.css";
import Photo from "./Photo";
import { DragDropContext } from "react-beautiful-dnd";

const images = [
  {
    id: 1,
    name: "Apple",
    img: apl,
  },
  {
    id: 2,
    name: "Android",
    img: and,
  },
  {
    id: 3,
    name: "PowerPoint",
    img: pwr,
  },
];

const ThirdQ = ({ incressRightAnswers, setResult }) => {
  const [imgState, setImgState] = useState(images);

  return (
    <div
      style={{
        display: "flex",
        height: "100%",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        color: "wheat",
        dir: "rtl",
      }}
    >
      <DragDropContext>
        <h1>שאלה 3:</h1>

        <div style={{ display: "flex" }}>
          {imgState.map(({ id, name, img }) => (
            <Photo img={img} name={name} id={id} key={id} />
          ))}
        </div>

        <div
          className="container"
          style={{
            border: "3px solid green",
            display: "flex",
            justifyContent: "space-between",
            width: "400px",
            height: "150px",
          }}
        ></div>
      </DragDropContext>
      <button
        className="button"
        onClick={() => {
          setResult(true);
          incressRightAnswers();
        }}
      >
        !!!!
      </button>
    </div>
  );
};

export default ThirdQ;

