import { useState } from "react";
import and from "../pics/android.png";
import apl from "../pics/apple.png";
import pwr from "../pics/power.png";

const images = [
  {
    id: 1,
    name: "Apple",
    src: "{../pics/apple.png}",
  },
  {
    id: 2,
    name: "Android",
    src: "{../pics/android.png}",
  },
  {
    id: 3,
    name: "PowerPoint",
    src: "{../pics/power.png}",
  },
];

const ThirdQ = ({ setResult }) => {
  return (
    <div
      style={{
        display: "flex",
        height: "100%",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        color: "wheat",
      }}
    >
      <h1>שאלה 3:</h1>
      <div>
        <img src={and} alt={"Android"} key={1} style={{ width: "100px", margin: "15px" }} />
        <img src={apl} alt={"Apple"} key={2} style={{ width: "100px", margin: "15px" }} />
        <img src={pwr} alt={"PowerPoint"} key={3} style={{ width: "100px", margin: "15px" }} />
      </div>

      <div style={{display:"flex", flexDirection:"column"}}>
        <div style={{ backgroundColor: "grey", width:"100px", height:"100px" }}> גרור לכאן</div>
        <div style={{ backgroundColor: "black", width:"100px", height:"100px" }}> גרור לכאן</div>
        <div style={{ backgroundColor: "red", width:"100px", height:"100px" }}> גרור לכאן</div>
      </div>

      <button className="button" onClick={() => setResult(true)}>
        !!!!
      </button>
    </div>
  );
};

export default ThirdQ;
