import { ButtonNext } from "pure-react-carousel";
import { useState } from "react";

const SecondQ = ({ incressRightAnswers }) => {
  const [isAnswered, setIsAnswered] = useState(false);
  let inputTxt = "";
  const ans = "אגדה";

  const handleClick = () => {
    setIsAnswered(true);

    if (!inputTxt.localeCompare(ans)) {
      incressRightAnswers();
      alert('ענית על התשובה נכון, לחץ המשך על מנת להתקדם');
    }
    else{
        alert('ענית על התשובה לא נכון, לחץ המשך על מנת להתקדם');
    }
  };

  return (
    <div
      style={{
        height: "100%",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        color: "wheat",
      }}
    >
      <h1>שאלה 2:</h1>
      <div>
        אם תרצו אין זו
        <input
          type="text"
          className="input"
          placeholder="השלם את המשפט"
          onChange={(e) => (inputTxt = e.target.value.trim())}
          disabled={isAnswered}
        />
      </div>
      <div>
        <button className="button" onClick={() => handleClick()} disabled={isAnswered} style={{cursor: "not-allowed"}}>
          בדוק
        </button>
        <ButtonNext className="button" disabled={!isAnswered}>
          המשך
        </ButtonNext>
      </div>
    </div>
  );
};

export default SecondQ;
