import "../style/Name.css";

const Name = ({ setUserName, setIsSubmitted }) => {
  const checkLegality = (text) => {
    if (text.trim().localeCompare("")) {
      return true;
    }
    return false;
  };
  let text = "";

  return (
    <div
      style={{
        display: "flex",
        height: "100%",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
      }}
    >
      <input
        type="text"
        className="input"
        placeholder="הכנס את שמך"
        onChange={(e) => {

          if(checkLegality(e.target.value))
          setUserName(e.target.value);
          else{
              return alert("AAA")
          }
        }}
        size="50"
      />
      <button
        className="button custButton2"
        onClick={() => {
          setIsSubmitted(true);

        }}
      >
        המשך
      </button>
    </div>
  );
};

export default Name;
