import { useState } from "react";
import and from "../pics/android.png";
import apl from "../pics/apple.png";
import pwr from "../pics/power.png";
import "../style/ThirdQ.css";

// const images = [
//   {
//     id: 1,
//     name: "Apple",
//     src: "{../pics/apple.png}",
//   },
//   {
//     id: 2,
//     name: "Android",
//     src: "{../pics/android.png}",
//   },
//   {
//     id: 3,
//     name: "PowerPoint",
//     src: "{../pics/power.png}",
//   },
// ];

const ThirdQ = ({ setResult }) => {
  const draggables = document.querySelectorAll(".draggable");
  const containers = document.querySelectorAll(".container");

  draggables.forEach((draggable) => {
    draggable.addEventListener("dragstart", () => {
      draggable.classList.add("dragging");
    });
    draggable.addEventListener("dragend", () => {
      draggable.classList.remove("dragging");
    });
  });

  containers.forEach((container) => {
    container.addEventListener("dragover", () => {
        const draggable = document.querySelector('.dragging')
        container.appendChild(draggable)
    });
  });

  return (
    <div
      style={{
        display: "flex",
        height: "100%",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        color: "wheat",
      }}
    >
      <h1>שאלה 3:</h1>

      <div className="container">
        <img
          src={and}
          alt={"Android"}
          key={1}
          style={{ width: "100px", margin: "15px" }}
          draggable="true"
          className="draggable"
        />
        <img
          src={apl}
          className="draggable"
          alt={"Apple"}
          key={2}
          style={{ width: "100px", margin: "15px" }}
        />
        <img
          src={pwr}
          className="draggable"
          alt={"PowerPoint"}
          key={3}
          style={{ width: "100px", margin: "15px" }}
        />
      </div>

      <div

        style={{ border: "3px solid green", display: "flex", justifyContent: "space-between" }}
      >
        <div className="container" style={{ backgroundColor: "grey", width: "100px", height: "100px", margin: "15px" }}>
          גרור לכאן
        </div>
        <div className="container" style={{ backgroundColor: "black", width: "100px", height: "100px", margin: "15px" }}>
          גרור לכאן
        </div>
        <div className="container" style={{ border:"3px solid red", width:"100px" }} />

      </div>

      <button className="button" onClick={() => setResult(true)}>
        !!!!
      </button>
    </div>
  );
};

export default ThirdQ;
