import { ButtonNext } from 'pure-react-carousel';
import { useState } from 'react';

const SecondQ = () => {
    const [isAnswered, setIsAnswered] = useState(false);
    let inputTxt = "";
    const ans = "אגדה";

    const handleClick = (inputTxt) => {
        setIsAnswered(true);
        // incressRightAnswers
    };

    // document.getElementById('foo').disabled = false;

    return (
        <div
            style={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center',
            }}
        >
            <h2>שאלה 2:</h2>
            <div>
                אם תרצו אין זו
                <input type='text' className='input' placeholder='השלם את המשפט' onChange={e => inputTxt = e.target.value} />
            </div>
            <div>
                <button className='button' onClick={handleClick(inputTxt)}>
                    בדוק
                </button>
                <ButtonNext className='button' disabled={!isAnswered}>
                    המשך
                </ButtonNext>
            </div>
        </div>
    );
};

export default SecondQ;
