import { useState } from "react";
import and from "../pics/android.png";
import apl from "../pics/apple.png";
import pwr from "../pics/power.png";
import "../style/ThirdQ.css";

// const images = [
//   {
//     id: 1,
//     name: "Apple",
//     src: "{../pics/apple.png}",
//   },
//   {
//     id: 2,
//     name: "Android",
//     src: "{../pics/android.png}",
//   },
//   {
//     id: 3,
//     name: "PowerPoint",
//     src: "{../pics/power.png}",
//   },
// ];

const ThirdQ = ({ incressRightAnswers ,setResult }) => {
//   const draggables = document.querySelectorAll(".draggable");
//   const containers = document.querySelectorAll(".container");

  const draggables = document.querySelectorAll('.draggable')
  const containers = document.querySelectorAll('.container')

  draggables.forEach(draggable => {
    draggable.addEventListener('dragstart', () => {
      draggable.classList.add('dragging')
    })

    draggable.addEventListener('dragend', () => {
      draggable.classList.remove('dragging')
    })
  })

  containers.forEach(container => {
    container.addEventListener('dragover', e => {
      e.preventDefault()
      const afterElement = getDragAfterElement(container, e.clientX)
      const draggable = document.querySelector('.dragging')
      if (afterElement == null) {
        container.appendChild(draggable)
      } else {
        container.insertBefore(draggable, afterElement)
      }
    })
  })


  function getDragAfterElement(container, y) {
    const draggableElements = [...container.querySelectorAll('.draggable:not(.dragging)')]

    return draggableElements.reduce((closest, child) => {
      const box = child.getBoundingClientRect()
      const offset = y - box.top - box.height / 2
      if (offset < 0 && offset > closest.offset) {
        return { offset: offset, element: child }
      } else {
        return closest
      }
    }, { offset: Number.NEGATIVE_INFINITY }).element
  }
  const func = () => incressRightAnswers();
  func();


  return (
    <div
      style={{
        display: "flex",
        height: "100%",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        color: "wheat",
        dir:"rtl"
      }}
    >
      <h1>שאלה 3:</h1>

      <div className="container">
        <img
          src={and}
          alt={"Android"}
          key={1}
          style={{ width: "100px", margin: "15px" }}
          draggable="true"
          className="draggable"
        />
        <img
          src={apl}
          className="draggable"
          alt={"Apple"}
          key={2}
          style={{ width: "100px", margin: "15px" }}
        />
        <img
          src={pwr}
          className="draggable"
          alt={"PowerPoint"}
          key={3}
          style={{ width: "100px", margin: "15px" }}
        />
      </div>

      <div
        className="container"
        style={{ border: "3px solid green", display: "flex", justifyContent: "space-between", width:"400px", height:"150px" }}
      >


      </div>

      <button className="button" onClick={() => setResult(true)}>
        !!!!
      </button>
    </div>
  );
};

export default ThirdQ;
