import { useState } from "react";
import and from "../pics/android.png";
import apl from "../pics/apple.png";
import pwr from "../pics/power.png";
import "../style/ThirdQ.css";
import Photo from "./Photo";
import TestComp from "./TestComp.js";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const images = [
  {
    id: 1,
    name: "Apple",
    img: apl,
  },
  {
    id: 2,
    name: "Android",
    img: and,
  },
  {
    id: 3,
    name: "PowerPoint",
    img: pwr,
  },
];

const ThirdQ = ({ incressRightAnswers, setResult }) => {
  const [imgState, setImgState] = useState(images);

  const onDragEnd = (res) => {
    //TODO
  };

  return (
    <div
      style={{
        display: "flex",
        height: "100%",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        color: "wheat",
        dir: "rtl",
      }}
    >
      <h1>שאלה 3:</h1>

      <DragDropContext onDragEnd={onDragEnd}>
        <div style={{ display: "flex" }}>
          {imgState.map(({ id, name, img }) => (
            <Draggable draggableId={id} index={"1"}>
              <Photo img={img} name={name} id={id} key={id} />
            </Draggable>
          ))}
        </div>

        <Droppable droppableId={"1"}>
          {(provided) => {
            <TestComp innerRef={provided.innerRef} {...provided.droppableProps}>
              {" "}
              {provided.placeholder}
            </TestComp>;
          }}
        </Droppable>
      </DragDropContext>

      <button
        className="button"
        onClick={() => {
          setResult(true);
          incressRightAnswers();
        }}
      >
        !!!!
      </button>
    </div>
  );
};

export default ThirdQ;

