import { useDrop } from "react-dnd";

const DropArea = ({ onDrop, answers}) => {
    const [dropRef] = useDrop({
        accept: 'Answer',
        drop: (item) => {
            onDrop(item)
        }
    })
    return ( 
         <div
         ref={dropRef}
          className="container"
          style={{
            border: "3px solid green",
            display: "flex",
            justifyContent: "space-between",
            width: "400px",
            height: "150px",
          }}
        >

            {answers.map(answer => <div></div>)}
        </div>
     );
}

export default DropArea
