import { useState } from "react";
import and from "../pics/android.png";
import apl from "../pics/apple.png";
import pwr from "../pics/power.png";
import "../style/ThirdQ.css";
import Photo from "./Photo";
import { DragDropContext, Droppable } from "react-beautiful-dnd";

const images = [
  {
    id: 1,
    name: "Apple",
    img: apl,
  },
  {
    id: 2,
    name: "Android",
    img: and,
  },
  {
    id: 3,
    name: "PowerPoint",
    img: pwr,
  },
];

const ThirdQ = ({ incressRightAnswers, setResult }) => {
  const [imgState, setImgState] = useState(images);

  const onDragEnd = (res) => {
    //TODO
  };

  return (
    <div
      style={{
        display: "flex",
        height: "100%",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column",
        color: "wheat",
        dir: "rtl",
      }}
    >
      <h1>שאלה 3:</h1>

      <DragDropContext onDragEnd={onDragEnd}>
        <div style={{ display: "flex" }}>
          {imgState.map(({ id, name, img }) => (
            <Photo img={img} name={name} id={id} key={id} />
          ))}
        </div>

        <Droppable droppableId={"1"}>
        {provided =>{
          <div
            ref={provided.innerRef}
            style={{
              border: "3px solid green",
              display: "flex",
              justifyContent: "space-between",
              width: "400px",
              height: "150px",
            }}
          ></div>
        }}

        </Droppable>
        <Droppable droppableId={"2"}>

        {() =>{
          <div
            className="container"
            style={{
              border: "3px solid green",
              display: "flex",
              justifyContent: "space-between",
              width: "400px",
              height: "150px",
            }}
          ></div>
        }}
        </Droppable>
        <Droppable droppableId={"3"}>
        {() =>{
          <div
            className="container"
            style={{
              border: "3px solid green",
              display: "flex",
              justifyContent: "space-between",
              width: "400px",
              height: "150px",
            }}
          ></div>
        }}
        </Droppable>
      </DragDropContext>

      <button
        className="button"
        onClick={() => {
          setResult(true);
          incressRightAnswers();
        }}
      >
        !!!!
      </button>
    </div>
  );
};

export default ThirdQ;

